-- Таблица: Category
DROP TABLE IF EXISTS Category;

CREATE TABLE Category (
    ID    INTEGER PRIMARY KEY AUTOINCREMENT,
    cName TEXT    COLLATE SYSTEMNOCASE
);


-- Таблица: CategoryView
DROP TABLE IF EXISTS CategoryView;

CREATE TABLE CategoryView (
    ID         INTEGER PRIMARY KEY AUTOINCREMENT,
