    cvMaxZoom  INTEGER
);


-- Таблица: Mark
DROP TABLE IF EXISTS Mark;

CREATE TABLE Mark (
    ID          INTEGER PRIMARY KEY AUTOINCREMENT,
    mCategory   INTEGER,
    mImage      INTEGER,
    mAppearance INTEGER,
    mName       TEXT    COLLATE SYSTEMNOCASE,
    mDesc       TEXT    COLLATE SYSTEMNOCASE,
    mGeoType    INTEGER,
