    mGeoLonSize INTEGER,
    mGeoLatSize INTEGER,
    mGeoWKB     BLOB
);


-- Таблица: MarkAppearance
DROP TABLE IF EXISTS MarkAppearance;

CREATE TABLE MarkAppearance (
    ID       INTEGER PRIMARY KEY AUTOINCREMENT,
    maColor1 INTEGER,
    maColor2 INTEGER,
    maScale1 INTEGER,
    maScale2 INTEGER
);


-- Таблица: MarkFTS
DROP TABLE IF EXISTS MarkFTS;

CREATE VIRTUAL TABLE MarkFTS USING fts4 (
    mName,
    mDesc,
